import React, {useState} from 'react'
import PropTypes from 'prop-types'

const styles = {
    form: {
        marginBottom: '20px',
    },

    input: {
        width: '400px',
        height: '50px',
        borderRadius: '10px',
        border: '1px solid gray',
        fontSize: '16px',
        outline: 'none',
        padding: '3px 15px',
        boxSizing: 'border-box'
    },

    button: {
        height: '50px',
        padding: '8px 20px',
        marginLeft: '20px',
        borderRadius: '10px',
        border: '1px solid gray',
        fontSize: '16px',
        cursor: 'pointer',
        outline: 'none',
        boxSizing: 'border-box'
    }
}

function AddTodo({ onCreate }) {
    const [value, setValue] = useState('')

    function submitHandler(event) {
        event.preventDefault()

        if(value.trim()) {
            onCreate(value)
            setValue("")
        }
    }

    return(
        <form style={styles.form} onSubmit={submitHandler}>
            <input type="text" style={styles.input} value={value} onChange={event => setValue(event.target.value)} />
            <button type="submit" style={styles.button}>Find!</button>
        </form>
    )
}

AddTodo.propTypes = {
    onCreate: PropTypes.func.isRequired
}

export default AddTodo
